var fs = require('fs');

function File() {
  function open(path, callback) {
		fs.readFile(path, 'utf-8', function (error, contents) {
			callback.call(this, contents);
		});
	}

  function save(path, contents) {
  	fs.writeFile(path, contents);
	}

  this.open = open;
  this.save = save;
}

module.exports = new File;